import React, { useState, useRef, useEffect } from "react";
import TodoList from "./TodoList";
import { v4 as uuidv4 } from "uuid";

function App() {
  const [todos, setTodos] = useState([]);
  const todoRef = useRef();
  const LOCAL_STORAGE_KEYS = "todolist.todo";

  function addTodo(e) {
    const name = todoRef.current.value;
    if (name === "") return;
    setTodos((prevtodos) => {
      return [...prevtodos, { id: uuidv4(), name: name, completed: false }];
    });
    todoRef.current.value = null;
  }
  useEffect(() => {
    const storedTodos = localStorage.getItem(LOCAL_STORAGE_KEYS);
  }, []);
  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEYS, JSON.stringify(todos));
  }, [todos]);

  return (
    <>
      <TodoList todoList={todos} />
      <input ref={todoRef} type="text" />
      <button onClick={addTodo}>Add new task</button>
      <button>Clear tasks</button>
      <div>You have 0 tasks left to do</div>
    </>
  );
}

export default App;
