import React, { useState, useRef, useEffect } from "react";
import TodoList from "./TodoList";
import { v4 as uuidv4 } from "uuid";

function App() {
  const [todos, setTodos] = useState([]);
  const todoRef = useRef();
  // const LOCAL_STORAGE_KEY = "tasks";

  function addTodo(e) {
    const name = todoRef.current.value;
    if (name === "") return;
    setTodos((prevtodos) => {
      return [...prevtodos, { id: uuidv4(), name: name, completed: false }];
    });
    todoRef.current.value = null;
  }
  useEffect(() => {
    const storedTodos = JSON.parse(localStorage.getItem(LOCAL_STORAGE_KEY));
    // console.log("ls is-> ", storedTodos);
    if (storedTodos) {
      console.log("setting storedTodos", storedTodos);
      setTodos(storedTodos);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(LOCAL_STORAGE_KEY, JSON.stringify(todos));
  }, [todos]);

  function toggleTodos(id) {
    const newTodos = [...todos];
    const todo = newTodos.find((todo) => todo.id === id);
    todo.completed = !todo.completed;
    setTodos(newTodos);
  }

  function handleClear() {
    const newTodos = todos.filter((todo) => !todo.completed);
    setTodos(newTodos);
  }
  return (
    <>
      <input ref={todoRef} type="text" />
      <button onClick={addTodo}>Add new task</button>
      <button onClick={handleClear}>Clear Completed Tasks</button>
      <div>
        You have {todos.filter((todo) => !todo.completed).length} tasks left to
        do
      </div>
      <TodoList todoList={todos} toggleTodos={toggleTodos} />
    </>
  );
}

export default App;
